import * as React from "react";
import Paper from "@material-ui/core/Paper";
import TableCell from "@material-ui/core/TableCell";
import {
  darken,
  fade,
  lighten,
} from "@material-ui/core/styles/colorManipulator";
import Typography from "@material-ui/core/Typography";
import { ViewState, EditingState } from "@devexpress/dx-react-scheduler";
import classNames from "clsx";
import {
  Scheduler,
  MonthView,
  WeekView,
  Appointments,
  Toolbar,
  DateNavigator,
  AppointmentTooltip,
  AppointmentForm,
  EditRecurrenceMenu,
  Resources,
  DragDropProvider,
} from "@devexpress/dx-react-scheduler-material-ui";
import WbSunny from "@material-ui/icons/WbSunny";
import FilterDrama from "@material-ui/icons/FilterDrama";
import Opacity from "@material-ui/icons/Opacity";
import ColorLens from "@material-ui/icons/ColorLens";
import { withStyles } from "@material-ui/core/styles";
import { owners } from "./demo-task/tasks";



// use axios in here to acquire these data
const appointments = [
  {
    id: 0,
    title: "Working",
    startDate: new Date(2021, 4, 3, 8, 0),
    endDate: new Date(2021, 4, 3, 12, 0),
    ownerId: 1,
  },
  {
    id: 1,
    title: "Working",
    startDate: new Date(2021, 4, 3, 14, 0),
    endDate: new Date(2021, 4, 3, 18, 0),
    ownerId: 2,
  },
  {
    id: 2,
    title: "Working",
    startDate: new Date(2021, 4, 4, 8, 0),
    endDate: new Date(2021, 4, 4, 12, 0),
    ownerId: 3,
  },
  {
    id: 3,
    title: "Working",
    startDate: new Date(2021, 4, 4, 14, 0),
    endDate: new Date(2021, 4, 4, 18, 0),
    ownerId: 4,
  },
  {
    id: 4,
    title: "Working",
    startDate: new Date(2021, 4, 5, 8, 0),
    endDate: new Date(2021, 4, 5, 12, 0),
    ownerId: 5,
  },
  {
    id: 5,
    title: "Working",
    startDate: new Date(2021, 4, 5, 14, 0),
    endDate: new Date(2021, 4, 5, 18, 0),
    ownerId: 6,
  },
  {
    id: 2,
    title: "Working",
    startDate: new Date(2021, 4, 6, 8, 0),
    endDate: new Date(2021, 4, 6, 12, 0),
    ownerId: 1,
  },
  {
    id: 3,
    title: "Working",
    startDate: new Date(2021, 4, 6, 14, 0),
    endDate: new Date(2021, 4, 6, 18, 0),
    ownerId: 3,
  },
  {
    id: 4,
    title: "Working",
    startDate: new Date(2021, 4, 7, 8, 0),
    endDate: new Date(2021, 4, 7, 12, 0),
    ownerId: 2,
  },
  {
    id: 5,
    title: "Working",
    startDate: new Date(2021, 4, 7, 14, 0),
    endDate: new Date(2021, 4, 7, 18, 0),
    ownerId: 4,
  },
  // {
  //   id: 5,
  //   title: "6",
  //   startDate: new Date(2021, 4, 5, 14, 0),
  //   endDate: new Date(2021, 4, 5, 18, 0),
  //   rRule: "FREQ=WEEKLY;BYDAY=FR;UNTIL=20210814",
  //   exDate: "20210713T100000Z,20210727T100000Z",
  //   ownerId: 2,
  // },
  // {
  //   id: 6,
  //   title: "7",
  //   startDate: new Date(2021, 4, 28, 12, 0),
  //   endDate: new Date(2021, 4, 28, 12, 30),
  //   rRule: "FREQ=WEEKLY;BYDAY=TH;UNTIL=20210727",
  //   exDate: "20210704T090000Z,20210719T090000Z",
  //   ownerId: 4,
  // },
  // {
  //   id: 7,
  //   title: "8",
  //   startDate: new Date(2021, 4, 3, 11, 0),
  //   endDate: new Date(2021, 4, 3, 12, 0),
  //   rRule: "FREQ=WEEKLY;BYDAY=TU;UNTIL=20210801",
  //   exDate: "20210710T080000Z,20210724T080000Z",
  //   ownerId: 3,
  // },
];

const resources = [
  {
    fieldName: "ownerId",
    title: "Owners",
    instances: owners,
  },
];

const getBorder = (theme) =>
  `1px solid ${
    theme.palette.type === "light"
      ? lighten(fade(theme.palette.divider, 1), 0.88)
      : darken(fade(theme.palette.divider, 1), 0.48)
  }`;

const DayScaleCell = (props) => (
  <MonthView.DayScaleCell
    {...props}
    style={{ textAlign: "center", fontWeight: "bold" }}
  />
);



// #FOLD_BLOCK
const CellBase = React.memo(
  ({
    classes,
    startDate,
    formatDate,
    otherMonth,
    // #FOLD_BLOCK
  }) => {
    const iconId = Math.abs(Math.floor(Math.sin(startDate.getDate()) * 10) % 3);
    const isFirstMonthDay = startDate.getDate() === 1;
    const formatOptions = isFirstMonthDay
      ? { day: "numeric", month: "long" }
      : { day: "numeric" };
    return (
      <TableCell
        tabIndex={0}
        className={classNames({
          [classes.cell]: true,
          [classes.rainBack]: iconId === 0,
          [classes.sunBack]: iconId === 1,
          [classes.cloudBack]: iconId === 2,
          [classes.opacity]: otherMonth,
        })}
      >
        <div className={classes.content}>
          {/* <WeatherIcon classes={classes} id={iconId} /> */}
        </div>
        <div className={classes.text}>
          {formatDate(startDate, formatOptions)}
        </div>
      </TableCell>
    );
  }
);

const TimeTableCell = withStyles(styles, { name: "Cell" })(CellBase);

const Appointment = withStyles(styles, { name: "Appointment" })(
  ({ classes, ...restProps }) => (
    <Appointments.Appointment {...restProps} className={classes.appointment} />
  )
);

const AppointmentContent = withStyles(styles, { name: "AppointmentContent" })(
  ({ classes, ...restProps }) => (
    <Appointments.AppointmentContent
      {...restProps}
      className={classes.apptContent}
    />
  )
);

// right corner LOGO and Company Name
const FlexibleSpace = withStyles(styles, { name: "ToolbarRoot" })(
  ({ classes, ...restProps }) => (
    <Toolbar.FlexibleSpace {...restProps} className={classes.flexibleSpace}>
      <div className={classes.flexContainer}>
        {/* <ColorLens fontSize="large" htmlColor="#FF7043" /> logo*/}
        <Typography variant="h4" style={{ marginLeft: "10px" }}>
          Company Name 
        </Typography>
      </div>
    </Toolbar.FlexibleSpace>
  )
);

class StaffShift extends React.PureComponent {
  // #FOLD_BLOCK
  constructor(props) {
    super(props);

    this.state = {
      data: appointments,
    };

    this.commitChanges = this.commitChanges.bind(this);
  }

  // put axios in here
  commitChanges({ added, changed, deleted }) {
    this.setState((state) => {
      let { data } = state;
      if (added) {
        const startingAddedId =
          data.length > 0 ? data[data.length - 1].id + 1 : 0;
        data = [...data, { id: startingAddedId, ...added }];
      }
      if (changed) {
        data = data.map((appointment) =>
          changed[appointment.id]
            ? { ...appointment, ...changed[appointment.id] }
            : appointment
        );
      }
      if (deleted !== undefined) {
        data = data.filter((appointment) => appointment.id !== deleted);
      }
      return { data };
    });
  }


  render() {
    const { data } = this.state;

    return (
      <Paper>
        <Scheduler data={data}>
          <EditingState onCommitChanges={this.commitChanges} />
          <ViewState defaultCurrentDate={Date()} />

          <MonthView
            // timeTableCellComponent={TimeTableCell} display color for every day
            dayScaleCellComponent={DayScaleCell}
          />

          <Appointments
            appointmentComponent={Appointment} //css style
            appointmentContentComponent={AppointmentContent}
          />
          <Resources data={resources} />

          <Toolbar flexibleSpaceComponent={FlexibleSpace} />
          <DateNavigator />

          <EditRecurrenceMenu />
          <AppointmentTooltip showCloseButton showDeleteButton showOpenButton />
          <AppointmentForm />
          <DragDropProvider />
        </Scheduler>
      </Paper>
    );
  }
}

export default StaffShift;
