  const { list } = message;

  return (
    <div className="message-display">
      {list.map((result, index) => (
        <div>
          <p>Hi {result.fullName}, I plan to arrange your shift in {result.data}</p>
          <button>Accept</button>
          <button>Refuse</button>
        </div>
      ))}
    </div>
  )
}

export default Notification;
